(88, 161, 1271317810, 1247589618, 0, 0, 0, 0, 0, 0, 'Crashkurs', 1251763200, 1254182400, '', 'Ein Crashkurs hat ein aufbauendes Pragramm für Tangoeinsteiger, die ein\r\nwenig Tangoluft Schnuppern wollen. Die Kurse vermitteln das\r\ngrundlegende Basiswissen des Tangotanzens und machen dich somit fitt\r\nfür den Einstieg in die Tango-Welt, sprich die Milongas und die\r\nPracticas.\r\n&nbsp;', 1, 1, 0, '', 68400, 72900, '', '', 0, '', '', 1, 0x33, '', '', 14, 90, 1),
(89, 161, 1271317947, 1247590070, 0, 0, 0, 0, 0, 0, 'Fortsetzungskurs', 1251763200, 1254182400, '', 'Der Fortsetzungskurs knüpft am geleiteten Unterricht der\r\nvorhergeganenen Crashkurses an und vertieft das Basiswissen über den\r\nTanz.\r\n&nbsp;', 1, 1, 0, '', 72900, 77400, '', '', 0, '', '', 1, 0x34, '', '', 14, 90, 1),
(90, 161, 1271317858, 1247590228, 0, 0, 0, 0, 0, 0, 'Workshop I (Technik)', 1252022400, 1254441600, '', 'Der Wokshop I richtet sich an TänzerInnen, die bereits ein Grundwissen\r\nim Tango haben (Crashkurs &amp; Fortsetzungskurs) und die weiter an\r\nihrer Technik im Tanz arbeiten wollen. Es werden verschiedene\r\nTanzelemente und Schrittkombinationen angeschaut und vertieft.&nbsp;', 1, 4, 0, '', 70200, 74700, '', '', 0, '', '', 3, 0x36, '', '', 14, 90, 2),
(91, 161, 1271317896, 1247590341, 0, 0, 0, 0, 0, 0, 'Workshop II (Dynamik im Tanz)', 1252022400, 1254441600, '', '<p>Der Workshop II richtet sich an TänzerInnen, die den Tango nicht vom\r\ntechnischen Aspekt beleuchten wollen, sondern sich mit\r\nInterpretationsmöglichkeiten, Stimmungen im Tanz und der damit\r\nverbundenen Musikalität auseinandersetzen möchten.<br />Für diesen Kurs empfehlen wir ein gutes Technik verständniss da er darauf aufbaut.&nbsp;</p>', 1, 4, 0, '', 74700, 79200, '', '', 0, '', '', 3, 0x36, '', '', 14, 90, 2),
